-- ----------------------------
-- Table structure for t_category
-- ----------------------------
CREATE TABLE `t_category` (
  `cid` int(11) NOT NULL AUTO_INCREMENT COMMENT '分类编号',
  `title` varchar(5) NOT NULL COMMENT '分类名称',
  `sequnce` int(11) DEFAULT '0' COMMENT '序列',
  `deleted` tinyint(1) NOT NULL DEFAULT '0' COMMENT '0:false;1:true',
  PRIMARY KEY (`cid`)
) ENGINE=InnoDB AUTO_INCREMENT=11 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Table structure for t_comment
-- ----------------------------
CREATE TABLE `t_comment` (
  `comid` int(11) NOT NULL AUTO_INCREMENT COMMENT '评论编号',
  `nid` int(11) NOT NULL COMMENT '新闻编号',
  `ptime` varchar(20) NOT NULL COMMENT '评论时间',
  `region` varchar(20) NOT NULL DEFAULT '未知地区' COMMENT '发布人地区',
  `content` varchar(500) NOT NULL COMMENT '评论内容',
  `supportcount` int(11) NOT NULL DEFAULT '0' COMMENT '支持数',
  `opposecount` int(11) NOT NULL DEFAULT '0' COMMENT '反对数',
  `deleted` tinyint(1) NOT NULL DEFAULT '0' COMMENT '0:false;1:true',
  PRIMARY KEY (`comid`),
  KEY `FK_t_comment_t_news` (`nid`),
  CONSTRAINT `t_comment_ibfk_1` FOREIGN KEY (`nid`) REFERENCES `t_news` (`nid`) ON DELETE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=11 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Table structure for t_news
-- ----------------------------
CREATE TABLE `t_news` (
  `nid` int(11) NOT NULL AUTO_INCREMENT COMMENT '新闻编号',
  `cid` int(11) NOT NULL COMMENT '新闻分类',
  `title` varchar(50) NOT NULL COMMENT '新闻标题',
  `digest` varchar(255) DEFAULT NULL COMMENT '摘要',
  `body` text NOT NULL COMMENT '新闻内容',
  `source` varchar(20) NOT NULL DEFAULT '互联网' COMMENT '新闻来源',
  `ptime` varchar(20) NOT NULL COMMENT '发布时间',
  `imgsrc` varchar(100) DEFAULT NULL COMMENT '新闻图片',
  `deleted` tinyint(1) NOT NULL DEFAULT '0' COMMENT '0:false;1:true',
  PRIMARY KEY (`nid`),
  KEY `FK_t_news_t_category` (`cid`),
  CONSTRAINT `t_news_ibfk_2` FOREIGN KEY (`cid`) REFERENCES `t_category` (`cid`)
) ENGINE=InnoDB AUTO_INCREMENT=43 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Table structure for userinfo
-- ----------------------------
CREATE TABLE `userinfo` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `username` varchar(64) NOT NULL,
  `pswd` varchar(64) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8;

